insert into MONITOR_ACTIVITY (MONITOR_ID, IKR_CATEGORY_RESOURCE_ID, ENABLE) (select MON.ID, RES.ID, 1 from MONITOR MON, IKR_CATEGORY_RESOURCE RES where METRIC_DOMAIN_CONFIG_ID in (select ID from METRIC_DOMAIN_CONFIG where IKR_STATIC_DOMAIN_ID in (select ID from IKR_STATIC_DOMAIN where DOMAIN_VALUE = 'JVM_ACTIVITY')) and RES.IKR_STATIC_DOMAIN_ID in (select ID from IKR_STATIC_DOMAIN where DOMAIN_VALUE like '%Memory Pool%'));